// Tests the distribution generated by Java's Math.random() function.
public  class  TestRandom {
	public static void main(String[]  args) {
		int NumRuns = Integer.parseInt(args[0]);
		int above = 0;
		int under = 0;
			// take random num and check and sum the ans per group
		for(int i=0; i<NumRuns;i++){
			if (Math.random()>0.5) above++;
			else under++;
		}
		//calc the ratio and print
		if (above!=0 && under!=0){
		 double ratio = (double) Math.min(above, under)/Math.max(above, under);
		System.out.println("> 0.5: "+ above + " times" );
		System.out.println(">= 0.5: "+ under + " times" );
		System.out.println("Ratio: "+ ratio);}
		else{
		System.out.println("> 0.5: "+ above + " times" );
		System.out.println(">= 0.5: "+ under + " times" );
		}


	}
}
